const data = $input.first().json;
const lookupData = $('Sheet Lookup').first().json;
const freshAlert = $('Extract Fields').first().json;

// Merge usernames (avoid duplicates)
const existingUsernames = lookupData.targetUsernames || "";
const newUsername = freshAlert.targetUsername || "";
let usernameArray = existingUsernames ? existingUsernames.split(", ") : [];
if (newUsername && usernameArray.indexOf(newUsername) === -1) {
  usernameArray.push(newUsername);
}

// Merge workstations (avoid duplicates)  
const existingWorkstations = lookupData.workstationNames || "";
const newWorkstation = freshAlert.workstationName || "";
let workstationArray = existingWorkstations ? existingWorkstations.split(", ") : [];
if (newWorkstation && workstationArray.indexOf(newWorkstation) === -1) {
  workstationArray.push(newWorkstation);
}

return {
  ...data,
  targetUsernames: usernameArray.join(", "),
  workstationNames: workstationArray.join(", ")
};

const rows = $input.all();

if (rows.length === 0) {
  return [{
    json: {
      totalIPs: 0,
      totalAttempts: 0,
      totalAlerts: 0,
      message: "‚úÖ No suspicious activity detected in the last period."
    }
  }];
}

// Calculate totals
const totalIPs = rows.length;
const totalAttempts = rows.reduce((sum, row) => sum + (row.json.totalAttempts || 0), 0);
const totalAlerts = rows.reduce((sum, row) => sum + (row.json.alertCount || 0), 0);

// Find top attackers (by attempts)
const topAttackers = rows
  .sort((a, b) => (b.json.totalAttempts || 0) - (a.json.totalAttempts || 0))
  .slice(0, 5)
  .map(row => ({
    ip: row.json.attackerIP,
    attempts: row.json.totalAttempts,
    country: row.json.countryCode,
    isp: row.json.isp,
    abuseScore: row.json.abuseScore
  }));

// Count by country
const countryCounts = {};
rows.forEach(row => {
  const country = row.json.countryCode || "Unknown";
  countryCounts[country] = (countryCounts[country] || 0) + 1;
});

const topCountries = Object.entries(countryCounts)
  .sort((a, b) => b[1] - a[1])
  .slice(0, 3);

// High-risk IPs (abuse score > 50 or TOR)
const highRiskIPs = rows.filter(row => 
  row.json.abuseScore > 50 || row.json.isTor === true
).length;

return [{
  json: {
    totalIPs,
    totalAttempts,
    totalAlerts,
    topAttackers,
    topCountries,
    highRiskIPs,
    reportTime: new Date().toISOString()
  }
}];

